loaded = False


class VersionInfo:
    version = "2014.2"
    release = "1.el7.centos"

    def release_string(self):
        return '%s-%s' % (self.version, self.release)

    def version_string(self):
        return self.version

version_info = VersionInfo()
version_string = version_info.version_string
